{
  "name": "feature-showcase",
  "version": "1.0.0",
  "description": "A simple CAP project to demonstrate different FEv4 List Report & Object Page features.",
  "repository": {
    "type": "git",
    "url": "git@github.com:SAP-samples/fiori-elements-feature-showcase.git"
  },
  "license": "Apache-2.0",
  "engines": {
    "node": ">=20"
  },
  "dependencies": {
    "@cap-js/hana": "^2",
    "@sap/cds": "^9",
    "@sap/xssec": "^4",
    "content-disposition": "^0.5.3",
    "express": "^4"
  },
  "devDependencies": {
    "cds-launchpad-plugin": "^2.0.1",
    "@cap-js/cds-typer": ">=0.1",
    "@cap-js/cds-types": "^0.14.0",
    "@cap-js/sqlite": "^2",
    "@eslint/js": "^9.35.0",
    "@eslint/json": "^0.13.2",
    "@sap/cds-dk": "^9",
    "@sap/eslint-plugin-cds": "^4.1.0",
    "@sap/ux-specification": "^1.136.5",
    "@types/node": "^22.0.0",
    "cds-plugin-ui5": "^0.13.4",
    "chai": "^6.2.0",
    "eslint": "^9.35.0",
    "globals": "^16.4.0",
    "jiti": "^2.5.1",
    "tsx": "^4",
    "typescript": "^5",
    "typescript-eslint": "^8.43.0"
  },
  "scripts": {
    "start": "cds-serve --production"
  },
  "sapux": [
    "app/listreport-objectpage",
    "app/worklist"
  ],
  "cds": {
    "launchpad": {
      "version": "1.140.0"
    },
    "requires": {
      "[production]": {
        "auth": "xsuaa",
        "db": "hana"
      },
      "destinations": true,
      "html5-repo": true,
      "workzone": true
    }
  },
  "imports": {
    "#cds-models/*": "./@cds-models/*/index.js"
  },
  "workspaces": [
    "app/*"
  ]
}
